import React, { Component } from "react";
import { Link, withRouter } from "react-router-dom";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { registerBusinessOwner } from "../../actions/authActions-Business";

import "./Auth.scss";

class Register extends Component {
/*   constructor() {
    super();
    this.state = {
      name: "",
      email: "",
      password: "",
      errors: {}
    };
  } */

  constructor() {
    super();
    this.state = {
      email: "",
      password: "",
      /* password2: "",       */
      name: "",
      businessName: "",
      businessAddress: "",
      errors: {}
    };
  }  


  componentDidMount() {
    // If logged in and user navigates to Register page, should redirect them to dashboard
    if (this.props.auth.isAuthenticated) {
      this.props.history.push("/dashboard");
    }
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({
        errors: nextProps.errors
      });
    }
  }

  onChange = e => {
    this.setState({ [e.target.id]: e.target.value });
  };

  onSubmit = e => {
    e.preventDefault();

    


    const newUser = {
      email: this.state.email,
      password: this.state.password,
      /* password2: this.state.password2, */
      name: this.state.name,
      businessName: this.state.businessName,
      businessAddress: this.state.businessAddress      
    };

    this.props.registerBusinessOwner(newUser, this.props.history);
  };


  render() {
    const { errors } = this.state;

    return (
      <div className="base-wrapper">
        <div className="auth-header">Register as a Business Owner</div>
        <form className="auth-form" noValidate onSubmit={this.onSubmit}>
          
          <div className="auth-group">
            <label>
              <div className="auth-label">Name</div>
              <input
                onChange={this.onChange}
                value={this.state.name}
                error={errors.name}
                id="name"
                type="text"
                className="auth-input"
              />
              <div className="auth-error">{errors.name}</div>
            </label>
          </div>

          <div className="auth-group">
            <label>
              <div className="auth-label">Email address</div>
              <input
                onChange={this.onChange}
                value={this.state.email}
                error={errors.email}
                id="email"
                type="email"
                className="auth-input"
              />
              <div className="auth-error">{errors.email}</div>
            </label>
          </div>

          <div className="auth-group">
            <label>
              <div className="auth-label">Password</div>
              <input
                onChange={this.onChange}
                value={this.state.password}
                error={errors.password}
                id="password"
                type="password"
                className="auth-input"
              />
              <div className="auth-error">{errors.password}</div>
            </label>
          </div>

          <div className="auth-group">
            <label>
              <div className="auth-label">Business Name</div>
              <input
                onChange={this.onChange}
                value={this.state.businessName}
                error={errors.businessName}
                id="businessName"
                type="text"
                className="auth-input"
              />
              <div className="auth-error">{errors.businessName}</div>
            </label>
          </div>

          <div className="auth-group">
            <label>
              <div className="auth-label">Business Address</div>
              <input
                onChange={this.onChange}
                value={this.state.businessAddress}
                error={errors.businessAddress}
                id="businessAddress"
                type="text"
                className="auth-input"
              />
              <div className="auth-error">{errors.businessAddress}</div>
            </label>
          </div>

          <div>
            <button type="submit" className="auth-button">
              Sign up
            </button>
          </div>
          <div className="bottom-group">
            <Link to="/" className="link">
              Sign in
            </Link>
          </div>
        </form>
      </div>
    );
  }
}

Register.propTypes = {
  registerBusinessOwner: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  auth: state.auth,
  errors: state.errors
});

export default connect(
  mapStateToProps,
  { registerBusinessOwner }
)(withRouter(Register));
